import React, {useState, useEffect} from "react";
import "./GamePage.scss"
import {GameBoard} from "../GameBoard/GameBoard";
import {Header} from "../Header/Header";
import {EndGame} from "../EndGame/EndGame";

export const GamePage = () => {
    const figures = [
        {
            figure: "ðŸ˜‡",
            id: 1
        },
        {
            figure: "ðŸ˜¡",
            id: 2
        },
        {
            figure: "ðŸ¤¡",
            id: 3
        },
        {
            figure: "ðŸ‘»",
            id: 4
        },
        {
            figure: "ðŸµ",
            id: 5
        },
        {
            figure: "ðŸ¹",
            id: 6
        },
        {
            figure: "ðŸ¯",
            id: 7
        },
        {
            figure: "ðŸ¦§",
            id: 8
        },
        {
            figure: "ðŸ",
            id: 9
        }]

    const [points, setPoints] = useState(0)
    const [moves, setMoves] = useState(0)
    const [blur, setBlur] = useState(false);
    // const [time, setTime] = useState(5)
    //
    // // useEffect(() => {
    // //     const interval = setInterval(() => {
    // //         setTime(prev => prev - 1)
    // //     }, 1000)
    // // }, [])

    const figuresArr = figures.concat(figures)
        // .sort(() => Math.random() - 0.5)

    useEffect(() => {
        if (points === 9) {
            setBlur(true)
        }
    }, [points])

    return (
        <>
            <Header/>
            <section className="game__page" style={blur ? {filter: "blur(4px)"} : {}}>
                <div className="container">
                    <div className="game__page-info">
                        <h1 className="game__page-time">
                            Time: 5 seconds
                        </h1>
                        <h1 className="game__page-points">
                            Score: {points} points
                        </h1>
                        <h1 className="game__page-points">
                            Moves: {moves} moves
                        </h1>
                    </div>
                    <GameBoard setPoints={setPoints} setMoves={setMoves} figuresArr={figuresArr}/>
                </div>
            </section>
            {
                blur && <EndGame/>
            }
        </>
    )
}
