import React, {useState, useEffect} from "react";

export const Card = ({card}) => {

    const [active, setActive] = useState(false)
    const handleActive = () => {
        setActive(!active)
    }

    return (
        <>
            <div className="card" onClick={handleActive}>
                {
                    active ?
                        <div className="card-front">
                            { card.figure }
                        </div>
                        :
                        <div className="card-back">

                        </div>
                }

            </div>

        </>
    )
}
