import React, {useState, useEffect} from "react";

export const Card = ({card, getID, matchedIndex}) => {
    const [active, setActive] = useState(false)

    const handleActive = () => {
        setActive(true)

        setTimeout(() => {
            setActive(false)
        }, 3000)
        getID(card.id)
    }

    return (
        <>
            <div className="card" onClick={handleActive}>
                {
                    active || matchedIndex.includes(card.id) ?
                        <div className="card-front">
                            { card.figure }
                        </div>
                        :
                        <div className="card-back">
                            M<span>G</span>
                        </div>
                }
            </div>
        </>
    )
}
