import React, {useState, useEffect} from "react";
import "./GameBoard.scss"
import {Card} from "../Card/Card";

export const GameBoard = ({setPoints, setMoves}) => {
    const figures = [
        {
            figure: "😇",
            id: 1
        },
        {
            figure: "😡",
            id: 2
        },
        {
            figure: "🤡",
            id: 3
        },
        {
            figure: "👻",
            id: 4
        },
        {
            figure: "🐵",
            id: 5
        },
        {
            figure: "🐹",
            id: 6
        },
        {
            figure: "🐯",
            id: 7
        },
        {
            figure: "🦧",
            id: 8
        },
        {
            figure: "🐍",
            id: 9
        }]

    const figuresArr = figures.concat(figures)


    const [index, setIndex] = useState([])
    const [matchedIndex, setMatchedIndex] = useState([])
    let sorted ;

    console.log(figuresArr)
    const getID = index => {
        setIndex(prev => [...prev, index])
    }
    useEffect(() => {
        sorted = figuresArr.sort(() => Math.random() - 0.5)
    }, [])

    useEffect(() => {
        if (index && index.length === 2) {
            setTimeout(() => {
                setIndex([])
            }, )
            if (index[0] === index[1]) {
                setMatchedIndex(prev => [...prev, index[0]])
                setPoints(prev => prev + 1)
            }
        }
    }, [index])

    console.log(index)
    return (
        <>
            <div className="game__board">
                {
                    sorted.map(figure => <Card card={figure} getID={getID} matchedIndex={matchedIndex} setMove={setMoves} index={index}/>)
                }
            </div>
        </>
    )
}
